(211, 'swiper', '<script type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/gsap/1.19.1/TweenMax.min.js\"></script>\r\n  <link rel=\"stylesheet\" href=\"https://unpkg.com/swiper/swiper-bundle.min.css\" />\r\n<script src=\"https://unpkg.com/swiper/swiper-bundle.min.js\"></script>\r\n<style>\r\n#dynadata300x50{\r\n        width: 300px;\r\n        height: 50px;\r\n        position: absolute;\r\n        left:0px;\r\n        top:0px;\r\n        user-select: none;\r\n        font-family:Ember;\r\n        cursor: pointer;\r\n        overflow: hidden;\r\n        box-sizing: border-box;\r\n        border:1px solid black;\r\n    }\r\n    .swiper-slide{\r\n       width: 110px;height: 48px;\r\n     }\r\n\r\n    .swiper-3d, .swiper-3d.swiper-css-mode .swiper-wrapper {\r\n        perspective:600px;\r\n    }\r\n    .swiper-3d .swiper-slide-shadow {\r\n        background-color: rgba(0, 0, 0, 0);\r\n    }\r\n    \r\n    .mock300x50{\r\n         position:absolute;top:1px;left:3px;width:100px;height:42px;box-shadow: 3px 3px 12px rgba(0,0,0,0.6);\r\n    }\r\n    \r\n</style>\r\n<body>\r\n<a target=\'_blank\' id=\"clickurl\" href=\"\">\r\n<div id=\"dynadata300x50\">\r\n    <img id =\'bg\' style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/bg.png\">\r\n    <img id =\'copy1\' style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/copy1.png\">\r\n    <img id =\'copy2\' style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/copy2.png\">\r\n    <img id=\"logo\" style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/logo.png\">\r\n    <img id=\"cta\" style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/cta.png\">\r\n\r\n    <div id=\"swipe_style300x50\" style=\"position:absolute;width: 110px;height: 48px;background-color:transparent;top:3px;left:5px;overflow:hidden;\">\r\n    \r\n        <div id=\"swiper\" class=\"swiper mySwiper\">\r\n            <div class=\"swiper-wrapper\" id=\"swiper-wrapper\">\r\n                <div class=\"swiper-slide\">\r\n                    <img class=\"mock300x50 st_card\" id=\"card1\" src=\"https://s.hcurvecdn.com/mnsauto/mnsautotest/ast/card1.webp\">\r\n                </div>\r\n                <div class=\"swiper-slide\">\r\n                    <img class=\"mock300x50 st_card\" id=\"card2\" src=\"https://s.hcurvecdn.com/mnsauto/mnsautotest/ast/card2.webp\">\r\n                </div>\r\n                <div class=\"swiper-slide\">\r\n                    <img class=\"mock300x50 st_card\" id=\"card3\" src=\"https://s.hcurvecdn.com/mnsauto/mnsautotest/ast/card3.webp\">\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>      \r\n</div>\r\n\r\n</a>\r\n</body>\r\n<script>\r\n  var swiper = new Swiper(\".mySwiper\", {\r\n    effect: \"cards\",\r\n    allowSlideNext:true,\r\n    grabCursor:true,\r\n       loop:true,\r\n    autoplay : true\r\n\r\n});\r\n\r\n  var swiper = new Swiper(\".mySwiper\", {\r\n        effect: \"cards\",\r\n        allowSlideNext:true,\r\n        grabCursor:true,\r\n        loop: true,\r\n        autoplay : true,\r\n    \r\n    });\r\n    \r\n    function stops(){\r\n        swiper.autoplay.stop();\r\n    }\r\n    setTimeout(stops, 27000);\r\n    </script>', '300x50', 'bg,logo,copy,card1,card2,card3', 'card_container_top,card_container_left,card_container_width,card_container_height,card_width,card_height,card_top,card_left,card_border_text,card-lp-text,box-shadow-text', '<script>\r\n    path = localStorage.getItem(\"path\");\r\n          var dim_url = path.split(\"/\")[2];\r\n          var dim_wd = dim_url.split(\"x\")[0];\r\n    if(document.getElementById(\"swipe_style\"+dim_url) == null){\r\n\r\nwindow.location.reload()\r\n}\r\n        document.getElementById(\"card_container