\r\n        document.getElementById(\"card-lp-text\").value = clkset ;\r\n       \r\n        }\r\n    }\r\n      \r\n    \r\n    \r\n    \r\n        document.getElementById(\"card-lp-text\").addEventListener(\"keyup\",() => {\r\n        \r\n        placeholder = document.getElementById(\"card-lp-text\").value;\r\n        if(placeholder!=\"\"){\r\n            i=0;\r\n          var lps = placeholder.split(\",\");\r\n        for(k=1;k<=document.getElementsByClassName(\"patte\"+dim_url).length;k++){\r\n         document.getElementById(\"card\"+k).setAttribute(\"onClick\",`my1(\"$lps[i]}\")`)\r\n     i++;\r\n        }\r\n        } \r\n        else{\r\n            for(k=1;k<=document.getElementsByClassName(\"patte\"+dim_url).length;k++){\r\n         document.getElementById(\"card\"+k).setAttribute(\"onClick\",\"\")\r\n     i++;\r\n        }\r\n        }\r\n      \r\n     })\r\n       \r\n        document.getElementById(\"card_container_top\").addEventListener(\"keyup\",(e)=>{\r\n            document.getElementById(\"swipe_style\"+dim_url).style.top = e.target.value+\"px\";\r\n        });\r\n        document.getElementById(\"card_container_left\").addEventListener(\"keyup\",(e)=>{\r\n            document.getElementById(\"swipe_style\"+dim_url).style.left = e.target.value+\"px\";\r\n        });\r\n        document.getElementById(\"card_container_width\").addEventListener(\"keyup\",(e)=>{\r\n            document.getElementById(\"swipe_style\"+dim_url).style.width = e.target.value+\"px\";\r\n        });\r\n        document.getElementById(\"card_container_height\").addEventListener(\"keyup\",(e)=>{\r\n            document.getElementById(\"swipe_style\"+dim_url).style.height = e.target.value+\"px\";\r\n            var swipeSilde = document.getElementsByClassName(\"swiper-slide\");\r\n            for (let jq = 0; jq < swipeSilde.length; jq++) {\r\n                swipeSilde[jq].style.height = e.target.value+\"px\";\r\n            }\r\n        })\r\n        \r\n        \r\n      