ector(\".test23\").remove();\r\n        }\r\n    }\r\n    slider.setAttribute(\"onclick\", \"event.preventDefault();\");\r\n    slider.setAttribute(\"style\", `background-image: url(\"https://s.hcurvecdn.com/tataaia/first/DC4/arrow.png\")`);\r\n    \r\n  //  slider[1].classList.remove(\"img-comp-slider\");\r\n\r\n    img.parentElement.insertBefore(slider, img);\r\n    slider.style.top = (h / 2) - (slider.offsetHeight / 2) + \"px\";\r\n    slider.style.left = (w / 2) - (slider.offsetWidth / 2) + \"px\";\r\n\r\n    slider.addEventListener(\"mousedown\", slideReady);\r\n    window.addEventListener(\"mouseup\", slideFinish);\r\n    slider.addEventListener(\"touchstart\", slideReady);\r\n    window.addEventListener(\"touchend\", slideFinish);\r\n    \r\n    function slideReady(e) {\r\n      e.preventDefault();\r\n      clicked = 1;\r\n      window.addEventListener(\"mousemove\", slideMove);\r\n      window.addEventListener(\"touchmove\", slideMove);\r\n    }\r\n    function slideFinish(e) {\r\n      clicked = 0;\r\n      e.preventDefault();\r\n    }\r\n    function slideMove(e) {\r\n     \r\n      var pos;\r\n      if (clicked == 0) return false;\r\n      pos = getCursorPos(e)\r\n      if (pos < 0) pos = 0;\r\n      if (pos > w) pos = w;\r\n      slide(pos);\r\n    //    e.preventDefault();\r\n    }\r\n    function getCursorPos(e) {\r\n    \r\n      var a, x = 0;\r\n      e = (e.changedTouches) ? e.changedTouches[0] : e;\r\n      a = img.getBoundingClientRect();\r\n      x = e.pageX - a.left;\r\n      x = x - window.pageXOffset;\r\n      return x;\r\n    //   e.preventDefault();\r\n    }\r\n    function slide(x,e) {\r\n      img.style.width = x + \"px\";\r\n      slider.style.left = img.offsetWidth - (slider.offsetWidth / 2) + \"px\";\r\n    }\r\n  }\r\n\r\n\r\n</script>\r\n\r\n  ', '300x250', 'card1,card2,card3', '', '<script>\r\n\r\n    if (localStorage.getItem(\"card_ast\") != \"\") {\r\n      let cards_asset = JSON.parse(localStorage.getItem(\"card_ast\"));\r\n      