(447, 'video', '<script type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/gsap/1.19.1/TweenMax.min.js\"></script>\r\n<style>\r\n    #dynadata300x600{\r\n        width:300px;\r\n        height:600px;\r\n        overflow:hidden;\r\n        border: 1px solid black;\r\n        position:absolute;\r\n        top: 0;\r\n        left: 0;\r\n        box-sizing: border-box;\r\n    }\r\n    #homescreen {\r\n        pointer-events: none;\r\n    }\r\n    \r\n</style>\r\n<a target=\"_blank\" id=\"clickurl\" href=\"\">\r\n<div id=\'dynadata300x600\'>\r\n     \r\n    <img style=\"position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc7/bg.png\">\r\n    <img style=\"position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc7/logo.png\">\r\n    <img id=\"copy1\" style=\"position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc7/copy1.png\">\r\n    <img id=\"copy2\" style=\"position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc7/copy2.png\">\r\n    \r\n    <img id=\"cta\" style=\"position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc7/cta.png\">\r\n</div>\r\n\r\n<div id=\"wrap\" style=\"position:absolute;top:286px;left:0px;z-index:1000;background:black;width:300px;height:171px;transform:rotate(0deg) skewX(0deg) skewY(0deg);\">\r\n        <div id=\"progress\" style=\"position:absolute;background:#ffffff;width:0%;height:3px;top:0px;opacity:.7\"></div>\r\n\r\n    <video style=\"margin-top:0px;width:300px\" autoplay HTMLMediaElement.autoplay = true id=\"homescreen\" muted=true playsinline poster=\"https://s.hcurvecdn.com/tanishq/campaign1/dc3/300x600/play.png\">\r\n            \r\n    <source src=\"https://do.hockeycurve.com/tanishq/campaign1/300x600/dc11/video.mp4\" type=\"video/mp4\">  \r\n        </video>\r\n\r\n       <div onclick=\"event.preventDefault();event.stopPropagation()\" style=\"width:100%;position:absolute;bottom:10px;text-align:right\">\r\n           <img onclick=\"replay(event)\" style=\"position:absolute;width:22px;right:40px\" src=\"https://s.hcurvecdn.com/discovery_plus/campaign1/replay.png\">\r\n            <img id=\"mute\" onclick=\"unmute(event)\" style=\"position:absolute;width:22px;right:10px\" src=\"https://s.hcurvecdn.com/discovery_plus/campaign1/mute.png\">\r\n            <span>&nbsp;</span>\r\n        </div>\r\n    </div>\r\n<input type=\"hidden\" value=\"0\" id=\"v_delay\" />\r\n</a>\r\n<div onclick=\"replay(event)\" style=\"width:30px;height:30px;background:black;opacity:0;left:0px;top:0px;position:absolute;z-index:1000;\">\r\n</div>\r\n\r\n\r\n<script>\r\nvar played = false;\r\nvar sound = false;\r\nvar vid = document.getElementById(\"homescreen\");\r\n\r\nfunction play(e){\r\n    if(played || vid.currentTime > 0){\r\n        console.log(\"manual play called\");\r\n        vid.muted = true;\r\n        return true;\r\n    }else{\r\n        e.preventDefault();\r\n        vid.play();\r\n        unmute();\r\n        return false;\r\n    }\r\n}\r\nvid.onended = function() {\r\n    played = true;\r\n    this.style[\"display\"] = \"none\";\r\n    document.getElementById(\"wrap\").style[\"display\"] = \"none\";\r\n};\r\n\r\nfunction updateProgress() {\r\n    var percent = (vid.currentTime*100/vid.duration) || 0;\r\n    document.getElementById(\"progress\").style[\"width\"] = percent+\"%\";\r\n    if(percent < 100) {\r\n        setTimeout(updateProgress,100);\r\n    }\r\n}\r\n\r\nupdateProgress()\r\n\r\nfunction replay(e){\r\n    if(e)e.preventDefault();\r\n    vid.muted=false;\r\n    sound = true;\r\n    document.getElementById(\"mute\").src = \"https://s.hcurvecdn.com/discovery_plus/campaign1/volume.png\";\r\n    vid.style[\"display\"] = \"block\";\r\n    document.getElementById(\"wrap\").style[\"display\"] = \"block\";\r\n    vid.currentTime = \"0\";\r\n    vid.play()\r\n}\r\nfunction unmute(e){\r\n    if(e)e.preventDefault();\r\n    if(!sound){\r\n        console.log(\"unmute\")\r\n        vid.muted=false\r\n        sound = true\r\n        document.getElementById(\"mute\").src = \"https://s.hcurvecdn.com/discovery_plus/campaign1/volume.png\"\r\n    }else{\r\n        console.log(\"mute\")\r\n        vid.muted=true\r\n        sound = false\r\n        document.getElementById(\"mute\").src =\"https://s.hcurvecdn.com/discovery_plus/campaign1/mute.png\"   \r\n    }\r\n    \r\n}\r\n\r\nif (Number(document.getElementById(\"v_delay\").value) >= 0) {\r\n    document.getElementById(\"wrap\").style.display = \"none\"; \r\n    function showVid() { document.getElementById(\"wrap\").style.display = \"block\"; } \r\n    setTimeout(showVid, Number(document.getElementById(\"v_delay\").value)*1000);   \r\n}\r\n</script>', '300x600', 'bg,copy,logo,cta,video', 'video_width,video_height,video_top,video_left,video_rotate,video_skewX,video_skewY,video_delay', '<script>\r\n\r\ndocument.getElementById(\"video_width\").value = Number(document.getElementById(\"wrap\").style.width.replace(/px/g, \'\'))\r\ndocument.getElementById(\"video_height\").value = Number(document.getElementById(\"wrap\").style.height.replace(/px/g, \'\'))\r\ndocument.getElementById(\"video_top\").value = Number(document.getElementById(\"wrap\").style.top.replace(/px/g, \'\'))\r\ndocument.getElementById(\"video_left\").value = Number(document.getElementById(\"wrap\").style.left.replace(/px/g, \'\'))\r\n\r\ndocument.getElementById(\"video_delay\").value = Number(document.getElementById(\"v_delay\").value)\r\n\r\ndocument.getElementById(\"video_rotate\").value = Number(document.getElementById(\"wrap\").style.transform.split(\" \")[0].replace(/rotate/g, \'\').replace(/deg/g, \'\').replace(\'(\', \'\').replace(\')\', \'\'))\r\n\r\ndocument.getElementById(\"video_skewX\").value = Number(document.getElementById(\"wrap\").style.transform.split(\" \")[1].replace(/skewX/g, \'\