ntent: creative_code.trim(),\r\n              fcat: fcat,\r\n              asset_r: localStorage.getItem(\"asset_req\"),\r\n          },\r\n          success : function(dt){\r\n              console.log(dt)\r\n          }\r\n      });      \r\n  }  \r\n\r\nvar card_ast = [];\r\n\r\nif(localStorage.getItem(\"assets\") != \"\" && localStorage.getItem(\"assets\") != null){\r\n          var asst_url = JSON.parse(localStorage.getItem(\"assets\"))\r\n          path = localStorage.getItem(\"path\")\r\n          count = localStorage.getItem(\"count\")\r\n          a_rq = localStorage.getItem(\"asset_req\")\r\n         \r\n          var dim_url = path.split(\"/\")[2]\r\n          var dim_wd = dim_url.split(\"x\")[0]\r\n          setTimeout(() => {\r\n              var box_dy = document.getElementById(\"dynadata\"+dim_url);\r\n              box_dy.innerHTML = \"\";\r\n              box_dy.innerHTML += `<div data-slide=\"slide\" class=\"slide\">\r\n                                   <div class=\"slide-items\" id=\"imgdiv\"></div>\r\n                                       <nav class=\"slide-nav\" id=\"loading\">\r\n                                         <div class=\"slide-thumb\"></div>\r\n                                         <button class=\"slide-prev\" id=\"left\"></button>\r\n                                         <button class=\"slide-next\" id=\"right\"></button>\r\n                                        </nav>\r\n                                    </div>`\r\n           \r\n              asst_url.forEach(el => {\r\n  \r\n                  var ast_id = el.Key.substring(el.Key.lastIndexOf(\'/\')+1).split(\'.\')[0]\r\n\r\n          \r\n                  if(ast_id.includes(\"card\")==true){\r\n                      a_rq.split(\",\").forEach(ar => {\r\n\r\n                      if(ar == ast_id){\r\n                          document.getElementById(\"imgdiv\").innerHTML += `<img id=\"${ast_id}\" style=\"position:absolute;width:${dim_wd}px;left:0;top:0px;\" src=\"https://s.hcurvecdn.com/${el.Key}?v=${el.LastModified}\">`\r\n                      }\r\n                  })\r\n                  }\r\n                  else if(ast_id.includes(\"left\")==true){\r\n                      document.getElementById(\"left\").innerHTML = `<img onclick=\"event.preventDefault()\" id=\"${ast_id}\" style=\"position:absolute;\" src=\"https://s.hcurvecdn.com/${el.Key}?v=${el.LastModified}\">`\r\n                  }else if(ast_id.includes(\"right\")==true){\r\n                      document.getElementById(\"right\").innerHTML = `<img onclick=\"event.preventDefault()\" id=\"${ast_id}\" style=\"position:absolute;\" src=\"https://s.hcurvecdn.com/${el.Key}?v=${el.LastModified}\">`\r\n                  }\r\n              });\r\n\r\n           insertCreative();\r\n          localStorage.setItem(\"assets\",\"\");\r\n          window.location.reload();\r\n          }, 1000);\r\n      }\r\n\r\n</script>\r\n\r\n\r\n');
