(457, 'cube', '<script type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/gsap/1.19.1/TweenMax.min.js\"></script>\r\n<style>\r\n#dynadata250x300,#test250x300{\r\n    width: 250px;\r\n    height: 300px;\r\n    position: absolute;\r\n    left:0px;\r\n    top:0px;\r\n    user-select: none;\r\n    cursor: pointer;\r\n    overflow: hidden;\r\n	box-sizing: border-box;\r\n	background: #b2beb5;\r\n	/*box-shadow: inset 0 0 1px #B3B3B3;*/\r\n	border: 1px solid black;\r\n}\r\n\r\n.scene {\r\n    position: absolute;\r\n  perspective: 800px;\r\n  height: 180px;\r\n  width: 180px;\r\n  top: 310px;\r\n left: 55px;\r\n}\r\n\r\n.cube {\r\n  transform-style: preserve-3d;\r\n  position: relative;\r\n  width: 180px;\r\n  height: 180px;\r\n  animation: rotate 15s ease-in-out 2;\r\n  transform-origin: center center;\r\n}\r\n\r\n.side {\r\n  position: absolute;\r\n  width: 180px;\r\n  height: 180px;\r\n  background-color: #333;\r\n  background-size: cover;\r\n  background-repeat: no-repeat;\r\n  border: none;\r\n}\r\n\r\n.back {\r\n  transform: translateZ(90px) rotateX(0deg);\r\n}\r\n\r\n.left {\r\n  transform: translateX(-90px) rotateY(-90deg);\r\n}\r\n\r\n.right {\r\n  transform: translateX(90px) rotateY(90deg);\r\n}\r\n\r\n.top {\r\n  transform: translateY(90px) rotateX(-90deg);\r\n}\r\n\r\n.bottom {\r\n  transform: translateY(-90px) rotateX(-270deg);\r\n}\r\n\r\n.front {\r\n  /*transform: translateZ(-64px) scaleY(-1);*/\r\n animation: rotatef 15s ease-in-out 2;\r\n}\r\n@keyframes rotatef {\r\n  0% { transform: rotate(180deg) translateZ(-90px) scaleY(-1);opacity:1;}\r\n  12.5% { transform: rotate(180deg) translateZ(-90px) scaleY(-1);opacity:1;}\r\n  25% { transform: rotate(180deg) translateZ(-90px) scaleY(-1);opacity:1;}\r\n  37.5% { transform: rotate(180deg) translateZ(-90px) scaleY(-1);opacity:1;}\r\n  37.6% { transform: rotate(180deg) translateZ(-90px) scaleY(-1);opacity:0;}\r\n  37.7% { transform: rotate(0deg) translateZ(-90px) scaleY(-1);opacity:0;}\r\n  50% { transform: rotate(0deg) translateZ(-90px) scaleY(-1);opacity:1;}\r\n  62.5% { transform: rotate(0deg) translateZ(-90px) scaleY(-1)}\r\n  75% { transform:rotate(0deg) translateZ(-90px) scaleY(-1)}\r\n  87.5% { transform: rotate(0deg) translateZ(-90px) scaleY(-1)}\r\n  100% { transform: rotate(0deg) translateZ(-90px) scaleY(-1)}\r\n}\r\n@keyframes rotate {\r\n  0% { transform: rotateX(0)}\r\n  12.5% { transform: rotateY(90deg)}\r\n  25% { transform: rotateY(180deg)}\r\n  37.5% { transform: rotateY(270deg)}\r\n  50% { transform: rotateY(360deg)}\r\n  62.5% { transform: rotateX(90deg)}\r\n  75% { transform: rotateX(180deg)}\r\n  87.5% { transform: rotateX(270deg)}\r\n  100% { transform: rotateX(360deg)}\r\n}\r\n</style>\r\n<a target=\"_blank\" href=\"\" id=\"clickurl\">\r\n<div id=\"test\">\r\n    <div id=\"dynadata250x300\"><img id=\"bg300x600\" style=\"position:absolute;width:320px;\" src=\"https://s.hcurvecdn.com/atest/tanishq/300x600/bg.png\"><img id=\"cta300x600\" style=\"position:absolute;width:320px;\" src=\"https://s.hcurvecdn.com/atest/tanishq/300x600/cta.png\"><img id=\"logo300x600\" style=\"position:absolute;width:320px;\" src=\"https://s.hcurvecdn.com/atest/tanishq/300x600/logo.png\"><img id=\"mr300x600\" style=\"position:absolute;width:320px;\" src=\"https://s.hcurvecdn.com/atest/tanishq/300x600/mr.png\"></div>\r\n\r\n    <div class=\"main\">        \r\n        <div class=\"scene\" id=\"scenes\">\r\n         <div class=\"cube\" id=\"cubes\">\r\n            <div class=\"side back\">\r\n                <img id=\"card1\" style=\"position:absolute;top:0px;left:0px;width:180px\" src=\"https://s.hcurvecdn.com/tanishq/campaign5/dc3/bangle.png\">\r\n            </div>\r\n            <div class=\"side left\">\r\n                <img id=\"card2\" style=\"position:absolute;top:0px;left:0px;width:180px\" src=\"https://s.hcurvecdn.com/tanishq/campaign5/dc3/chainpen.png\">\r\n            </div>\r\n            <div class=\"side right\">\r\n                <img id=\"card3\" style=\"position:absolute;top:0px;left:0px;width:180px\" src=\"https://s.hcurvecdn.com/tanishq/campaign5/dc3/earings.png\">\r\n            </div>\r\n            <div class=\"side top\">\r\n                <img id=\"card4\" style=\"position:absolute;top:0px;left:0px;width:180px\" src=\"https://s.hcurvecdn.com/tanishq/campaign5/dc3/earpen.png\">\r\n            </div>\r\n            <div class=\"side bottom\">\r\n                <img id=\"card5\" style=\"position:absolute;top:0px;left:0px;width:180px\" src=\"https://s.hcurvecdn.com/tanishq/campaign5/dc3/pendant.png\">\r\n            </div>\r\n            <div class=\"side front\">\r\n                <img id=\"card6\" style=\"position:absolute;top:0px;left:0px;width:180px\" src=\"https://s.hcurvecdn.com/tanishq/campaign5/dc3/ring.png\"> </div>\r\n          </div>\r\n        \r\n        </div>\r\n    </div>\r\n      </div>     \r\n</a>', '250x300', 'bg,copy,logo,cta,kv,card1,card2,card3,card4', 'cube-top,cube-left,cube-scale', '<script type=\"text/javascript\">\r\n  var imgtop = document.getElementById(\"card4\")\r\n  var imgbottom = document.getElementById(\"card5\")\r\n  var imgright = document.getElementById(\"card3\")\r\n  var imgleft = document.getElementById(\"card2\")\r\n  var imgfront = document.getElementById(\"card6\")\r\n  var imgback = document.getElementById(\"card1\")\r\n  \r\n  setTimeout(() => {\r\n      let cube_wrap = document.querySelectorAll(\".side\");\r\n              console.log(cube_wrap.length)\r\n              cube_wrap.innerHTML = \"\";\r\n      if(localStorage.getItem(\"card_ast\") != \"\"){\r\n      var cards_asset = JSON.parse(localStorage.getItem(\"card_ast\"));\r\n      if(cards_asset.length == 6){\r\n          imgback.src = \"https://s.hcurvecdn.com/\"+cards_asset[0].key+\"?v=\"+cards_asset[0].modified;\r\n          imgleft.src = \"https://s.hcurvecdn.com/\"+cards_asset[1].key+\"?v=\"+cards_asset[1].modified;\r\n          imgright.src = \"https://s.hcurvecdn.com/\"+cards_asset[2].key+\"?v=\"+cards_asset[2].modified;\r\n          imgtop.src = \"https://s.hcurvecdn.com/\"+cards_asset[3].key+\"?v=\"+cards_asset[3].modified;\r\n          imgbottom.src = \"https://s.hcurvecdn.com/\"+cards_asset[4].key+\"?v=\"+cards_asset[4].modified;\r\n          imgfront.src = \"https://s.hcurvecdn.com/\"+cards_asset[5].key+\"?v=\"+cards_asset[5].modified;\r\n      }else if(cards_asset.length == 5){\r\n          imgback.src = \"https://s.hcurvecdn.com/\"+cards_asset[0].key+\"?v=\"+cards_asset[0].modified;\r\n          imgleft.src = \"https://s.hcurvecdn.com/\"+cards_asset[1].key+\"?v=\"+cards_asset[2].modified;\r\n          imgright.src = \"https://s.hcurvecdn.com/\"+cards_asset[2].key+\"?v=\"+cards_asset[2].modified;\r\n          imgtop.src = \"https://s.hcurvecdn.com/\"+cards_asset[3].key+\"?v=\"+cards_asset[3].modified;\r\n          imgbottom.src = \"https://s.hcurvecdn.com/\"+cards_asset[4].key+\"?v=\"+cards_asset[4].modified;\r\n          imgfront.src = \"https://s.hcurvecdn.com/\"+cards_asset[0].key+\"?v=\"+cards_asset[0].modified;\r\n\r\n}else if(cards_asset.length == 4){\r\n          imgback.src = \"https://s.hcurvecdn.com/\"+cards_asset[0].key+\"?v=\"+cards_asset[0].modified;\r\n          imgleft.src = \"https://s.hcurvecdn.com/\"+cards_asset[1].key+\"?v=\"+cards_asset[1].modified;\r\n          imgright.src = \"https://s.hcurvecdn.com/\"+cards_asset[2].key+\"?v=\"+cards_asset[2].modified;\r\n          imgtop.src = \"https://s.hcurvecdn.com/\"+cards_asset[3].key+\"?v=\"+cards_asset[3].modified;\r\n          imgbottom.src = \"https://s.hcurvecdn.com/\"+cards_asset[3].key+\"?v=\"+cards_asset[3].modified;\r\n          imgfront.src = \"https://s.hcurvecdn.com/\"+cards_asset[0].key+\"?v=\"+cards_asset[0].modified;\r\n\r\n}if(cards_asset.length == 3){\r\n          imgback.src = \"https://s.hcurvecdn.com/\"+cards_asset[0].key+\"?v=\"+cards_asset[0].modified;\r\n          imgleft.src = \"https://s.hcurvecdn.com/\"+cards_asset[1].key+\"?v=\"+cards_asset[1].modified;\r\n          imgright.src = \"https://s.hcurvecdn.com/\"+cards_asset[2].key+\"?v=\"+cards_asset[2].modified;\r\n          imgtop.src = \"https://s.hcurvecdn.com/\"+cards_asset[4].key+\"?v=\"+cards_asset[4].modified;\r\n          imgbottom.src = \"https://s.hcurvecdn.com/\"+cards_asset[3].key+\"?v=\"+cards_asset[3].modified;\r\n          imgfront.src = \"https://s.hcurvecdn.com/\"+cards_asset[0].key+\"?v=\"+cards_asset[0].modified;\r\n\r\n}}\r\n      }, 1000);\r\n\r\n      \r\n\r\n      document.getElementById(\"cube-top\").addEventListener(\'keyup\',() => {\r\n          cubetop = document.getElementById(\"cube-top\").value; \r\n          document.getElementById(\"scenes\").style.marginTop = cubetop+\"px\";\r\n      })\r\n  \r\n      document.getElementById(\"cube-left\").addEventListener(\'keyup\',() => {\r\n          cubeleft = document.getElementById(\"cube-left\").value; \r\n          document.getElementById(\"scenes\").style.marginLeft = cubeleft+\"px\";\r\n      })\r\n  \r\n      document.getElementById(\"cube-scale\").addEventListener(\'keyup\',() => {\r\n          cubescale = document.getElementById(\"cube-scale\").value; \r\n          document.getElementById(\"cubes\").style.scale= cubescale;\r\n      })\r\n\r\n  document.getElementById(\"cube-top\").value = Number(document.getElementById(\"scenes\").style.marginTop.replace(/px/g, \'\'));\r\n  document.getElementById(\"cube-left\").value = Number(document.getElementById(\"scenes\").style.marginLeft.replace(/px/g, \'\'));\r\n  document.getElementById(\"cube-scale\").value = Number(document.getElementById(\"cubes\").style.scale);\r\n\r\n  </script>', ''),
(458, 'scratch', '<script type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/gsap/1.19.1/TweenMax.min.js\"></script>\r\n<script type=\"text/javascript\" src=\"https://cdn.jsdelivr.net/npm/canvas-confetti@1.5.1/dist/confetti.browser.min.js\"></script>\r\n<style>\r\n    * {\r\n        margin: 0px;\r\n    }\r\n    #dynadata300x250 {\r\n        width: 300px;\r\n        height: 250px;\r\n        position: absolute;\r\n        left: 0px;\r\n        top: 0px;\r\n        user-select: none;\r\n        border: 1px solid black;\r\n        cursor: pointer;\r\n        overflow: hidden;\r\n        box-sizing: border-box;\r\n    }\r\n\r\n    #scratch300x250 {\r\n        top: 0px;\r\n        left: 0px;\r\n        background-size: contain;\r\n        background-repeat: no-repeat;\r\n        position: absolute;\r\n        width: 300px;\r\n        height: 250px;\r\n        cursor: pointer;\r\n        /* border: 1px solid black; */\r\n    }\r\n</style>\r\n\r\n\r\n    <a target=\'_blank\' id=\"clickurl\" href=\"\"\r\n        style=\"width:300px;height:250px;overflow:hidden\">\r\n        <div id=\"dynadata300x250\">\r\n            <img id=\"bg300x250\" style=\"position:absolute;top:0px;left:0px;width:300px;\"\r\n                src=\"https://hcurvecdn.com/swiggy/swigy/dc12/bg2.png\">\r\n            <img id=\"bg2300x250\" style=\"position:absolute;top:0px;left:0px;width:300px;\"\r\n                src=\"https://hcurvecdn.com/swiggy/swigy/dc12/bg1.png\">\r\n\r\n\r\n\r\n\r\n\r\n        </div>\r\n<div id=\"conf300x250\" style=\"position:absolute;width:300px;height:250px;\"></div>\r\n\r\n    </a>\r\n    <canvas id=\"scratch300x250\" style=\"width:300px;height:250px\" onpointerdown=\"asach(), abc()\"></canvas>\r\n\r\n\r\n\r\n\r\n<script>\r\n    var sct = document.getElementById(\"bg2300x250\").src;\r\n    \r\n    var bridge = document.getElementById(\"scratch300x250\"),\r\n        bridgeCanvas = bridge.getContext(\"2d\"),\r\n        brushRadius = (bridge.width / 100) * 5,\r\