(477, 'spinwheel-multiple-offers', '\r\n    <script src=\"https://cdnjs.cloudflare.com/ajax/libs/Glide.js/3.0.1/glide.js\"></script>\r\n    <script type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/gsap/1.19.1/TweenMax.min.js\"></script>\r\n    <link rel=\"stylesheet\" href=\"https://cdnjs.cloudflare.com/ajax/libs/Glide.js/3.0.2/css/glide.core.css\">\r\n    <script src=\"https://cdn.jsdelivr.net/npm/canvas-confetti@1.5.1/dist/confetti.browser.min.js\"></script>\r\n    <style>\r\n    \r\n    #dynadata300x250{\r\n    width: 300px;\r\n    height: 250px;\r\n    position: absolute;\r\n    left:0px;\r\n    top:0px;\r\n    user-select: none;\r\n    cursor: pointer;\r\n    overflow: hidden;\r\n    border:1px solid black;\r\n    box-sizing: border-box;\r\n    }\r\n    \r\n    #copy{\r\n        z-index: 1;\r\n    }\r\n    .roulette_center {\r\n          position: absolute;\r\n          top: 255px;\r\n          left: 258px;\r\n          cursor: pointer;\r\n        \r\n      }\r\n      .roulette_center:hover {\r\n        transform: scale(1.01);\r\n        \r\n      }\r\n    \r\n      .roulette_wheel{\r\n        touch-action:auto;pointer-events:painted\r\n      }\r\n\r\n      #btn{\r\n    z-index:1000; \r\n    width:69px;\r\n    position: absolute;\r\n    top: 145px;\r\n    left: 6px;\r\n    } \r\n    #wheel{\r\n    position:absolute;\r\n    top:16px;\r\n    width:233px;\r\n    left:32px;\r\n    }\r\n    \r\n    </style>\r\n    \r\n    <a target=\"_blank\" href=\"\" id=\"clickurl\">\r\n     <div id=\"dynadata300x250\" class=\"align1\">  \r\n       \r\n        <img id=\"bg\" style=\"position:absolute;top:0px;left:0px;width:300px;height: 250px;\" src=\"https://s.hcurvecdn.com/rapido/spin/300x250/bg.png\">\r\n        <img id=\"logo\" style=\"position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/rapido/spin/300x250/logo.png\">\r\n    \r\n       <div id=\"f1\">\r\n        <img id=\"copy\" style=\"position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/atest/mahesh/spin/copy.png\">\r\n        <img id=\"wheel\" class=\"roulette_wheel\" id=\"wheel\" src=\"https://s.hcurvecdn.com/rapido/spin/300x250/cut-Spinwheel.png\">\r\n        <img id=\"arrow\" style=\"position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/atest/mahesh/spin/arrow.png\">\r\n        <img id=\"panel\" style=\"position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/atest/mahesh/spin/panel.png\">\r\n        <div onclick=\"event.preventDefault();event.stopPropagation()\" id=\"cta\">\r\n            <img onclick=\"roulette_spin(this)\" onfocus=\"transform()\" class=\"roulette_center\" id=\"btn\" src=\"https://s.hcurvecdn.com/atest/mahesh/spin/cta.png\">\r\n        </div>\r\n      </div>\r\n       \r\n       <div id=\"frames\">\r\n        <img id=\"cta2\" style=\"position:absolute;top:0px;left:0px;width:300px;display:none;\" src=\"https://s.hcurvecdn.com/rapido/camp1/300x250/spin/f2/cta.png\">\r\n        <img id=\"copy2\" style=\"position: absolute;top:0px;left:0px;width:300px;display:none;\" src=\"https://s.hcurvecdn.com/rapido/camp1/300x250/spin/f2/copy.png\">\r\n        <img id=\"frame2\" style=\"position:absolute;top:0px;left:0px;width:300px;display:none;\" src=\"https://s.hcurvecdn.com/rapido/camp1/300x250/spin/f2/card_1.png\">\r\n        <img id=\"frame3\" style=\"display:none;position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/atest/jasmeet/wheeljs/frame3.png\">\r\n        <img id=\"frame4\" style=\"display:none;position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/atest/jasmeet/wheeljs/frame4.png\">\r\n        <img id=\"frame5\" style=\"display:none;position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/atest/jasmeet/wheeljs/frame5.png\">\r\n        <img id=\"frame6\" style=\"display:none;position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/atest/jasmeet/wheeljs/frame6.png\">\r\n        <img id=\"frame7\" style=\"display:none;position:absolute;top:0px;left:0px;width:300px\" src=\"https://s.hcurvecdn.com/atest/jasmeet/wheeljs/frame7.png\">\r\n    </div>\r\n       <div id=\"conf\" style=\"position:absolute;\"></div>\r\n    </div> \r\n    </a>\r\n    \r\n    <script>\r\n      var force = 0;\r\n      var random = Math.floor(Math.random()*6);\r\n      var angle = 60 * random;\r\n      var inertia = 0.985;\r\n      var minForce = 15;\r\n      var rouletteElem = document.getElementsByClassName(`roulette_wheel`)[0];\r\n      var fnumber=\"frame\"+(random+2);\r\n      function roulette_spin(btn) {\r\n        force = 6.5 + minForce;\r\n        requestAnimationFrame(doAnimation);\r\n        document.getElementById(\"btn\").onclick=\"\";\r\n      }\r\n    \r\n      function doAnimation() {\r\n        angle = (angle + force) % 360;\r\n        force *= inertia;\r\n        rouletteElem.style.transform = `rotate(` + angle+ `deg)`;\r\n        if (force <= 0.05) {\r\n          document.getElementById(fnumber).style.display=\"block\";\r\n          document.getElementById(\"btn\").style.display=\"none\";\r\n          document.getElementById(\"cta2\").style.display=\"block\";\r\n          document.getElementById(\"copy\").style.display=\"none\";\r\n          document.getElementById(\"copy2\").style.display=\"block\";\r\n          document.getElementById(\"f1\").style.display=\"none\";\r\n          document.getElementById(\"wheel\").style.display=\"none\";\r\n          conff();\r\n          return;\r\n        }\r\n        requestAnimationFrame(doAnimation);\r\n      }\r\n    \r\n    function conff() {\r\n        var myCanvas = document.createElement(`canvas`);\r\n        var wid = document.getElementById(\"bg\").offsetWidth;\r\n        var hei = document.getElementById(\"bg\").offsetHeight;\r\n        document.getElementById(\"conf\").appendChild(myCanvas);\r\n        myCanvas.style.width = wid+\"px\";\r\n        myCanvas.style.zIndex = `6000`;\r\n        myCanvas.style.height = hei+\"px\";\r\n        var myConfetti = confetti.create(myCanvas, {\r\n          resize: true,\r\n          useWorker: true\r\n        });\r\n        myConfetti({\r\n          particleCount: 1000,\r\n          spread: 2100,\r\n          startVelocity: 30\r\n        });\r\n      }\r\n    \r\n    \r\n    </script>\r\n    \r\n   \r\n   ', '300x250', 'bg,copy,cta,logo,panel,wheel,arrow,copy2,cta2,offer1,offer2,offer3,offer4,offer5,offer6', 'wheel-top,wheel-left,wheel-scale,btn-top,btn-left,btn-scale', '<script>\r\n  \r\n\r\n    document.getElementById(\"wheel-top\").addEventListener(\'keyup\',() => {\r\n            wheeltop = document.getElementById(\"wheel-top\").value; \r\n                document.getElementById(\"wheel\").style.top = wheeltop+\"px\";\r\n    })\r\n    \r\n    document.getElementById(\"wheel-left\").addEventListener(\'keyup\',() => {\r\n            wheelleft = document.getElementById(\"wheel-left\").value; \r\n                document.getElementById(\"wheel\").style.left = wheelleft+\"px\";\r\n    })\r\n    \r\n    document.getElementById(\"wheel-scale\").addEventListener(\'keyup\',() => {\r\n            wheelscale = document.getElementById(\"wheel-scale\").value; \r\n                document.getElementById(\"wheel\").style.scale = wheelscale;\r\n    })\r\n    \r\n    \r\n    \r\n    document.getElementById(\"btn-top\").addEventListener(\'keyup\',() => {\r\n        btntop = document.getElementById(\"btn-top\").value; \r\n                document.getElementById(\"btn\").style.top = btntop+\"px\";\r\n    })\r\n    \r\n    document.getElementById(\"btn-left\").addEventListener(\'keyup\',() => {\r\n        btnleft = document.getElementById(\"btn-left\").value; \r\n                document.getElementById(\"btn\").style.left = btnleft+\"px\";\r\n    })\r\n    \r\n    document.getElementById(\"btn-scale\").addEventListener(\'keyup\',() => {\r\n        btnscale = document.getElementById(\"btn-scale\").value; \r\n                