n        background:#99999A;\r\n    }\r\n	\r\n	.list-item{\r\n	    box-sizing: border-box;\r\n		position: relative;\r\n		left:0px;\r\n		top:0px;\r\n		display: inline-block;\r\n	}\r\n	.img{\r\n	    position:absolute;\r\n	    top:3px;\r\n	    left:6px;\r\n	    width: 80px;\r\n		height: 89px;\r\n		background-size: contain;\r\n    	background-repeat: no-repeat;\r\n    	text-align: center;\r\n    	z-index:500;\r\n    	border: 0.1px solid gray;\r\n    }\r\n    .btn--shimmer {\r\n       animation: shimmer 3s linear 10;\r\n       animation-delay: 1.2s;\r\n       background-image: linear-gradient(-65deg, rgba(255, 255, 255, 0) 46%, rgba(255, 255, 255, .8) 50%, rgba(255, 255, 255, .8) 42%, rgba(255, 255, 255, 0) 56%);\r\n       background-size: 400% 110%;\r\n    }\r\n    @keyframes shimmer {\r\n    0% {\r\n    background-position: 100% 50%;\r\n     }\r\n    30% {\r\n    background-position: 0% 50%;\r\n    }\r\n    }  \r\n</style>\r\n<a target=\'_blank\' id=\"clickurl\" href=\"\">\r\n<div id=\"dynadata300x250\"> \r\n    <img style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/bg.png\" />\r\n    <img style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/logo.png\" />\r\n    <img id=\"copy\" style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/copy.png\" />\r\n    <img id=\"cta\" style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/cta.png\" />\r\n</div>\r\n      \r\n    \r\n    <div onclick=\"event.preventDefault();event.stopPropagation()\">\r\n        \r\n        <div onclick=\"show1()\" style=\"position:absolute;left:21px;top:90px;border-radius: 15px;width:253px;height:30px;border:2px solid rgba(0,0,0, .7);font-size:11px;background-color:#fff;\">\r\n            \r\n        <div id=\"s1\" style=\"position:absolute;top:8px;left:8px;font-size:15px;color:rgba(0,0,0, .7);letter-spacing:1;font-family:aperm\">Choose your product</div>\r\n            \r\n        <div style=\"position:absolute;top:0px;right:0px;width:32px;height:30px;\"> \r\n        <img id=\"arr1\" style=\"width: 17px;padding: 10px 7px 12px 7px;\" src=\"https://hcurvecdn.com/hoichoi/firstcampaign/300x250/DC7_F1_F2/down_arrow.png\">\r\n        </div>\r\n        </div>\r\n    \r\n        <div id=\"sub1\" class=\"search2\" style=\"position:absolute;left:21px;top:125px;z-index:102;text-decoration:none;display:none\">\r\n        <ul class=\"dropdown\" id=\"dropdown\"></ul>\r\n        </div>\r\n    </div>\r\n    \r\n    <div onclick=\"event.preventDefault();event.stopPropagation()\">\r\n        \r\n        <div onclick=\"show2()\" style=\"position:absolute;left:21px;top:140px;border-radius: 15px;width:253px;height:30px;border:2px solid rgba(0,0,0, .7);font-size:11px;background-color:#fff;\">\r\n            \r\n        <div id=\"s2\" style=\"position:absolute;top:8px;left:8px;font-size:15px;color:rgba(0,0,0, .7);letter-spacing:1;font-family:aperm\">Choose Shoe</div>\r\n            \r\n        <div style=\"position:absolute;top:0px;right:0px;width:32px;height:30px;\"> \r\n        <img id=\"arr2\" style=\"width: 17px;padding: 10px 7px 12px 7px;\" src=\"https://hcurvecdn.com/hoichoi/firstcampaign/300x250/DC7_F1_F2/down_arrow.png\">\r\n        </div>\r\n        </div>\r\n    \r\n        <div id=\"sub2\" class=\"search2\" style=\"position:absolute;left:21px;top:176px;z-index:102;text-decoration:none;display:none\">\r\n        <ul class=\"dropdown\" id=\"dropdown2\">\r\n        </ul>\r\n        </div>\r\n    </div>\r\n    <input type=\"hidden\" id=\"feed_dt\" />\r\n</a>\r\n<script type=\"text/javascript\">\r\n\r\nvar gd1 = \"\";\r\nvar gd2 = \"\";\r\nvar value1 = \"\"\r\nvar value2 = \"\";\r\ntry {\r\n    var feed_data = JSON.parse(document.getElementById(\"feed_dt\").value);\r\n} catch (error) {\r\n    var feed_data = {}\r\n}\r\n\r\nfunction show1() {\r\n    if(document.getElementById(\"sub1\").style.display != \"block\") {\r\n        document.getElementById(\"sub2\").style.display = \"none\";\r\n        document.getElementById(\"arr2\").style.transform = \"rotate(0deg)\";\r\n        document.getElementById(\"sub1\").style.display = \"block\";\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(180deg)\";\r\n    } else {\r\n        document.getElementById(\"sub1\").style.display =\"none\";\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n    }\r\n}\r\n\r\nfunction change1(id){ \r\n    value1 = document.getElementById(id).getAttribute(\"value\");\r\n    gd1 = document.getElementById(id).innerHTML;\r\n    document.getElementById(\"sub1\").style.display=\"none\";\r\n    document.getElementById(\"s1\").innerHTML= gd1;\r\n    document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n    document.getElementById(\"s2\").style.fontSize = \"15px\";\r\n    document.getElementById(\"s2\").innerHTML = \"Choose Store\";\r\n    for(let i in feed_data){\r\n        if(id == i){\r\n            changeDp2value(id,feed_data[i],gd1)\r\n        }\r\n    }\r\n}\r\n\r\nfunction show2() {\r\n    if(document.getElementById(\"dropdown2\").innerHTML != \"\"){\r\n    if(document.getElementById(\"sub2\").style.display != \"block\") {\r\n        document.getElementById(\"sub1\").style.display = \"none\";\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n        document.getElementById(\"sub2\").style.display =\"block\";\r\n        document.getElementById(\"arr2\").style.transform = \"rotate(180deg)\";\r\n    } else {\r\n        document.getElementById(\"sub2\").style.display =\"none\";\r\n        document.getElementById(\"arr2\").style.transform = \"rotate(0deg)\";\r\n    }}\r\n}\r\n\r\nfunction change2(id){ \r\n    value2 = document.getElementById(id).getAttribute(\"value\");\r\n    gd2 = document.getElementById(id).innerHTML;\r\n    document.getElementById(\"sub2\").style.display=\"none\";\r\n    document.getElementById(\"s2\").innerHTML= gd2;\r\n    document.getElementById(\"s2\").style.fontSize = \"14px\";\r\n    document.getElementById(\"arr2\").style.transform = \"rotate(0deg)\";\r\n}\r\nfunction changeDp2value(id,value,name) {\r\n    document.getElementById(\"dropdown2\").innerHTML = \"\";\r\n    value.forEach((el,i) => {\r\n        document.getElementById(\"dropdown2\").innerHTML += `<li id=\"${id}${i}\">${el.title}</li>`\r\n        document.getElementById(id+i).setAttribute(\"onclick\",`my1(\"${el.landing_url}\")`)\r\n    });\r\n}\r\n</script>', '300x250', 'bg,copy,cta,logo', 'dropdown1_name_text,dropdown2_name_text', '<script>\r\n    document.getElementById(\"dropdown1_name_text\").value = document.getElementById(\"s1\").innerText;\r\n    document.getElementById(\"dropdown2_name_text\").value = document.getElementById(\"s2\").innerText;\r\n\r\n    document.getElementById(\"dropdown1_name_text\").addEventListener(\"keyup\",(e)=>{\r\n        document.getElementById(\"s1\").innerText = e.target.value\r\n    })\r\n\r\n    document.getElementById(\"dropdown2_name_text\").addEventListener(\"keyup\",(e)=>{\r\n        document.getElementById(\"s2\").innerText = e.target.value\r\n    })\r\n</script>', '<script>\r\n    function insertCreative() {\r\n        \r\n        document.getElementById(\"s1\").innerText = document.getElementById(\"dropdown1_name_text\").value\r\n        document.getElementById(\"dropdown2\").innerHTML=\"\";\r\n        document.getElementById(\"sub1\").style.display =\"none\";\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n        document.getElementById(\"sub2\").style.display =\"none\";\r\n        document.getElementById(\"arr2\").style.transform = \"rotate(0deg)\";\r\n\r\n        var creative_code = $(\"#aspectRatio\").html();\r\n      $.ajax({\r\n        url: \"insertcreative.php\",\r\n        type: \"POST\",\r\n        dataType: \"JSON\",\r\n        data: {\r\n          id: id,\r\n          count: count,\r\n          content: creative_code.trim(),\r\n          fcat: fcat,\r\n          asset_r: localStorage.getItem(\"asset_req\"),\r\n        },\r\n        success: function (dt) {\r\n          console.log(dt);\r\n        },\r\n      });\r\n    }\r\n\r\n \r\n    if (localStorage.getItem(\"assets\") != \"\" && localStorage.getItem(\"assets\") != null) {\r\n      var asst_url = JSON.parse(localStorage.getItem(\"assets\"));\r\n      path = localStorage.getItem(\"path\");\r\n      count = localStorage.getItem(\"count\");\r\n      a_rq = localStorage.getItem(\"asset_req\");\r\n \r\n      var dim_url = path.split(\"/\")[2];\r\n      var dim_wd = dim_url.split(\"x\")[0];\r\n      var box_dy = document.getElementById(\"dynadata\" + dim_url);\r\n setTimeout(() => {\r\n    box_dy.innerHTML = \"\"; \r\n\r\n    async function loadApi() {\r\n                        var response = await fetch(\"https://publisherplex.io/selfserve/feed/multifeed.php?id=\"+feed_url);\r\n                        \r\n                        var data = await response.json();\r\n                        \r\n                        return data;\r\n                    }\r\n                    \r\n                    loadApi().then(res => {\r\n                        feed_data = res;\r\n                        console.log(res)\r\n                        document.getElementById(\"feed_dt\").value = JSON.stringify(feed_data); \r\n                        var dp1 = document.getElementById(\"dropdown\")\r\n                        dp1.innerHTML = \"\"\r\n                        for(let i in feed_data){\r\n                            dp1.innerHTML += `<li id=\"${i}\" onclick=\"change1(this.id)\">${i}</li>`;\r\n                        }\r\n        insertCreative();\r\nwindow.location.reload();\r\n                    })\r\n\r\n        asst_url.forEach((el) => {\r\n \r\n        var ast_id = el.Key.substring(el.Key.lastIndexOf(\"/\") + 1).split(\".\")[0];\r\n        a_rq.split(\",\").forEach(ar => {\r\n            if(ar == ast_id){\r\n                document.getElementById(\"dynadata300x250\").innerHTML += `<img id=\"${ast_id+dim_url}\" style=\"position:absolute;width:${dim_wd}px;left:0;top:0px;\" src=\"https://s.hcurvecdn.com/${el.Key}?v=${el.LastModified}\">`\r\n            }\r\n        }) \r\n        localStorage.setItem(\"assets\", \"\");\r\n        \r\n     });\r\n },2000);\r\n    }\r\n\r\n    \r\n </script>'),
(527, 'feed_carousel_manual', '<script type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/gsap/1.19.1/TweenMax.min.js\"></script>\r\n  <link rel=\"stylesheet\" href=\"https://unpkg.com/swiper/swiper-bundle.min.css\" />\r\n<script src=\"https://unpkg.com/swiper/swiper-bundle.min.js\"></script>\r\n<style>\r\n#dynadata300x250{\r\n        width: 300px;\r\n        height: 250px;\r\n        position: absolute;\r\n        left:0px;\r\n        top:0px;\r\n        user-select: none;\r\n        font-family: serif;\r\n        cursor: pointer;\r\n        overflow: hidden;\r\n        box-sizing: border-box;\r\n        border:1px solid black;\r\n    }\r\n    .swiper-slide{\r\n       width:147px;\r\n       height: 196px;\r\n     }\r\n\r\n    .swiper-3d, .swiper-3d.swiper-css-mode .swiper-wrapper {\r\n        perspective:600px;\r\n    }\r\n    .swiper-3d .swiper-slide-shadow {\r\n        background-color: rgba(0, 0, 0, 0);\r\n    }\r\n    \r\n    .mock{\r\n        position:absolute;top:13px;left:12px;width:119px;height:176px;\r\n    }\r\n    \r\n</style>\r\n<body>\r\n<a target=\'_blank\' id=\"clickurl\" href=\"\" style=\"width:300px;height:250px;overflow:hidden\">\r\n<div id=\"dynadata300x250\">\r\n    <img id =\'bg\' style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/bg.png\">\r\n    <img id =\'copy1\' style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/copy1.png\">\r\n    <img id =\'copy2\' style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/copy2.png\">\r\n    <img id=\"logo\" style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/logo.png\">\r\n    <img id=\"cta\" style=\'position:absolute;top:0px;left:0px;width:300px\' src=\"https://s.hcurvecdn.com/marks/valentine/300x250/cta.png\">\r\n\r\n    <div id=\"swipe_style\" style=\"pos