         price[ggg].style.top = e.target.value+\"px\";  \r\n        }\r\n    });\r\n    document.getElementById(\"price-left\").addEventListener(\"keyup\",(e)=>{\r\n        var price = document.getElementsByClassName(\"price\")\r\n        for (let fff= 0; fff< price.length; fff++) {\r\n            price[fff].style.left = e.target.value+\"px\";  \r\n        }\r\n    });\r\n    document.getElementById(\"price-color\").addEventListener(\"change\",(e)=>{\r\n        var price = document.getElementsByClassName(\"price\")\r\n        for (let afff= 0; afff< price.length; afff++) {\r\n            price[afff].style.color = e.target.value;  \r\n        }\r\n    });\r\n    document.getElementById(\"mrp-color\").addEventListener(\"change\",(e)=>{\r\n        var mrp = document.getElementsByClassName(\"mrp\")\r\n        for (let qfff= 0; qfff< mrp.length; qfff++) {\r\n            mrp[qfff].style.color = e.target.value;  \r\n        }\r\n    });\r\n    document.getElementById(\"title-color\").addEventListener(\"change\",(e)=>{\r\n        var title = document.getElementsByClassName(\"title\")\r\n        for (let wfff= 0; wfff< title.length; wfff++) {\r\n            title[wfff].style.color = e.target.value;  \r\n        }\r\n    });\r\n\r\n    \r\n    document.getElementById(\"font-family-text\").addEventListener(\"input\",(e)=>{\r\n        //   for (let f = 0; f < family.length; f++) {\r\n        //     family[f].style.fontFamily = e.target.value;  \r\n        //   }\r\n        document.getElementById(\"dynadata300x250\").style.fontFamily=e.target.value;  \r\n        } );\r\n    </script>\r\n    ', '<script>\r\n    function insertCreative() {\r\n      var creative_code = $(\"#aspectRatio\").html();\r\n      $.ajax({\r\n        url: \"insertcreative.php\",\r\n        type: \"POST\",\r\n        dataType: \"JSON\",\r\n        data: {\r\n          id: id,\r\n          count: count,\r\n          content: creative_code.trim(),\r\n          fcat: fcat,\r\n          asset_r: localStorage.getItem(\"asset_req\"),\r\n        },\r\n        success: function (dt) {\r\n          console.log(dt);\r\n        },\r\n      });\r\n    }\r\n\r\n    var feed_data = [];\r\n \r\n \r\n    if (localStorage.getItem(\"assets\") != \"\" && localStorage.getItem(\"assets\") != null) {\r\n      var asst_url = JSON.parse(localStorage.getItem(\"assets\"));\r\n      path = localStorage.getItem(\"path\");\r\n      count = localStorage.getItem(\"count\");\r\n      a_rq = localStorage.getItem(\"asset_req\");\r\n \r\n      var dim_url = path.split(\"/\")[2];\r\n      var dim_wd = dim_url.split(\"x\")[0];\r\n      var box_dy = document.getElementById(\"dynadata\" + dim_url);\r\n setTimeout(() => {\r\n    box_dy.innerHTML = \"\";\r\n\r\n       \r\n\r\n    box_dy.innerHTML += `<div id=\"swipe_style\" style=\"position:absolute;width:147px;height:197px;background-color:transparent;top:27px;left:5px;overflow:hidden;\"><div id=\"swiper\" class=\"swiper mySwiper\">\r\n                              <div class=\"swiper-wrapper\" id=\"swiper-wrapper\">\r\n                              </div>\r\n                          </div></div>`; \r\n \r\n\r\n    async function loadApi() {\r\n                        var response = await fetch(\"https://publisherplex.io/selfserve/feed/feed.php?id=\"+feed_url);\r\n                        \r\n                        var data = await response.json();\r\n                        \r\n                        return data;\r\n                    }\r\n                    \r\n                    loadApi().then(res => {\r\n                        feed_data = res;\r\n                          feed_data.forEach((el,i) => {\r\n                            Object.keys(el).forEach(key =>{\r\n                              if(key == \"image\"){\r\n                                document.getElementById(\"swiper-wrapper\").innerHTML += `<div id=\"side_${i}\" class=\"swiper-slide\">\r\n                                    <div id=\"imgsd${i}\" class=\"test55\" style=\"background-color:white;width: 98%;height: 95%;z-index: 1;margin-top: 4%;box-shadow: 3px 3px 12px rgba(0,0,0,0.6);\">\r\n                                      <img style=\"position:absolute;width:69%;height:66%;left:18px;object-fit:cover;z-index:1\" class=\"mock\" id=\"card${i}\" src=\"${el.image}\"><div id=\"feedtext${i}\" style=\"position: absolute;text-align: center;\"></div>\r\n                                    </div>  </div>`;\r\n                              }\r\n                              else if(key == \"url\"){\r\n                                document.getElementById(`side_${i}`).setAttribute(\"onclick\",`my1(\"${el.url}\");event.preventDefault();`)\r\n                              }\r\n                              else if(key == \"title\" && el.title !==\"\"){\r\n\r\n                                document.getElementById(`feedtext${i}`).innerHTML += `<span class=\"title family\" style=\"position: absolute;color: black;z-index: 1;width: 113px;font-size: 11px;left: 3px;top: 12px;text-align: center;text-overflow: ellipsis; overflow: hidden;white-space: nowrap;\">\r\n                            `+el.title+`</span>`;                            \r\n                                    }\r\n                                    else if(key == \"mrp\" && el.mrp !==\"\"){\r\n\r\n                                        document.getElementById(`feedtext${i}`).innerHTML += `<span class=\"mrp family\" style=\"position: absolute;color: black;z-index: 1;width:119px;font-size: 12px;left: 0px;top: 40px;text-align: center;font-weight:bold;\">\r\n                            &#8377; `+el.mrp+`</span>`;                               \r\n                                              }\r\n                                    \r\n           else if(key == \"price\" && el.price !==\"\"){\r\n\r\n            document.getElementById(`feedtext${i}`).innerHTML += `<span class=\"price family\" style=\"position: absolute;z-index: 1;color: grey;width: 119px;font-size: 9px;left:0px;top: 30px;text-align: center;font-weight:bold;\"><strike>\r\n                                           &#8377; `+el.price+`</strike></span>`;      }\r\n                                                \r\n                            })\r\n                          })\r\n                    })\r\n        asst_url.forEach((el) => {\r\n \r\n        var ast_id = el.Key.substring(el.Key.lastIndexOf(\"/\") + 1).split(\".\")[0];\r\n        a_rq.split(\",\").forEach(ar => {\r\n            if(ar == ast_id){\r\n                document.getElementById(\"dynadata300x250\").innerHTML += `<img id=\"${ast_id+dim_url}\" style=\"position:absolute;width:${dim_wd}px;left:0;top:0px;\" src=\"https://s.hcurvecdn.com/${el.Key}?v=${el.LastModified}\">`\r\n            }\r\n        }) \r\n        insertCreative();\r\n        localStorage.setItem(\"assets\", \"\");\r\n        // window.location.reload();\r\n     });\r\n },2000);\r\n    }\r\n\r\n    \r\n </script>');
