(544, 'mutlifeed_dropdown_city', '<script type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/gsap/1.19.1/TweenMax.min.js\"></script>\r\n<style>\r\n \r\n	#dynadata300x250{\r\n		width: 300px;\r\n		height: 250px;\r\n		position: absolute;\r\n		left:0px;\r\n		top:0px;\r\n		user-select: none;\r\n		border:1px solid black;\r\n		cursor: pointer;\r\n		overflow: hidden;\r\n		box-sizing: border-box;\r\n	}\r\n	\r\n	a{\r\n	    text-decoration: none;\r\n	}\r\n	\r\n	.search2{\r\n        width: 273px;\r\n        min-height: 58px;\r\n        max-height: 94px;\r\n        border-radius:5px;\r\n        z-index: 1000;\r\n        font-family: \"metroR\";\r\n        background-color: white;\r\n        cursor: pointer;\r\n        user-select: none;\r\n    }\r\n    .dropdown{\r\n        list-style-type: none;\r\n        padding: 0;\r\n        margin: 0;\r\n        overflow: auto;\r\n        width: 273px;\r\n        min-height: 35px;\r\n        max-height: 60px;\r\n        border: 1px solid rgba(0,0,0, .8);\r\n        background-color: white;\r\n        border-radius: 5px;\r\n   }\r\n\r\n    .dropdown  li  {\r\n        padding: 9px;\r\n        text-decoration: none;\r\n        font-size: 14px;\r\n        color: #000;\r\n        display: block;\r\n   }\r\n    li:hover{\r\n        background-color: #e8e8e8;\r\n   }\r\n   \r\n    .hr{\r\n        padding: 0 !important;\r\n        margin: 0 !important;\r\n        width:85%;\r\n        height:1px;\r\n        border-width:0;\r\n        color:gray;\r\n        background-color:#e8e8e8;\r\n        position:relative;\r\n        left:9px;\r\n    }\r\n\r\n    .dropdown::-webkit-scrollbar {\r\n        width: 5px;\r\n    }\r\n    .dropdown::-webkit-scrollbar-track {\r\n        background:  #EBEBEB;\r\n        border: 4px solid transparent;\r\n        background-clip: content-box;   \r\n    }\r\n    .dropdown::-webkit-scrollbar-thumb {\r\n        background:#99999A;\r\n    }\r\n</style>\r\n<a target=\'_blank\' id=\"clickurl\" href=\"\">\r\n<div id=\"dynadata300x250\"> \r\n    <img style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/bg.png\" />\r\n    <img style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/logo.png\" />\r\n    <img id=\"copy\" style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/copy.png\" />\r\n    <img id=\"cta\" style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/cta.png\" />\r\n</div>\r\n      \r\n    \r\n    <div id=\"main_box\" onclick=\"event.preventDefault();event.stopPropagation()\" style=\"position: absolute;top: 80px;\">\r\n        \r\n        <div onclick=\"show1()\" id=\"box_dp\" style=\"display:flex; align-items:center; justify-content:space-between;padding:0 10px;border-radius: 5px;width:253px;height:30px;border:2px solid rgba(0,0,0, .7);font-size:11px;background-color:#fff;\">\r\n            \r\n        <div id=\"s1\" style=\"font-size:15px;color:rgba(0,0,0, .7);letter-spacing:1;font-family:aperm\">Choose your product</div>\r\n        <img id=\"arr1\" style=\"width: 17px;padding: 10px 7px 12px 7px;\" src=\"https://hcurvecdn.com/hoichoi/firstcampaign/300x250/DC7_F1_F2/down_arrow.png\">\r\n        </div>\r\n    \r\n        <div id=\"sub1\" class=\"search2\" style=\"z-index:102;text-decoration:none;display:none\">\r\n        <ul class=\"dropdown\" id=\"dropdown\"></ul>\r\n        </div>\r\n    </div>\r\n    <input type=\"hidden\" id=\"feed_dt\" />\r\n    <input type=\"hidden\" id=\"deff_cy\" />\r\n</a>\r\n<script type=\"text/javascript\">\r\n\r\nvar gd1 = \"\";\r\nvar value1 = \"\"\r\n\r\nvar feed_data = document.getElementById(\"feed_dt\").value\r\nif(feed_data != null && feed_data != \"\"){\r\n    setTimeout(()=>{\r\n        var fd = JSON.parse(feed_data)\r\n        document.getElementById(\"dropdown\").innerHTML = \"\";\r\n        var keyyyy = Object.keys(fd);\r\n        live_city = document.getElementById(\"city_name\").innerText;\r\n        if(keyyyy.includes(live_city)){\r\n            for (const key in fd) {\r\n                if(live_city == key){\r\n                    fd[key].forEach((el,i) => {\r\n                        document.getElementById(\"dropdown\").innerHTML += `<li id=\"dp${i}\" onclick=\"change1(this.id)\">${el.title}</li>`;\r\n                        document.getElementById(\"dp\"+i).setAttribute(\"onclick\",`hide();my1(\"${el.landing_url}\")`)\r\n                    })\r\n                }\r\n            }\r\n        }else{\r\n            document.getElementById(\"city_name\").innerHTML = document.getElementById(\"deff_cy\").value;\r\n            for (const key in fd) {\r\n                if(document.getElementById(\"deff_cy\").value == key){\r\n                    fd[key].forEach((el,i) => {\r\n                        document.getElementById(\"dropdown\").innerHTML += `<li id=\"dp${i}\" onclick=\"change1(this.id)\">${el.title}</li>`\r\n                        document.getElementById(\"dp\"+i).setAttribute(\"onclick\",`hide();my1(\"${el.landing_url}\")`)\r\n                    })\r\n                }\r\n            }\r\n        }\r\n        \r\n    },1000)\r\n}\r\n\r\nfunction show1() {\r\n    if(document.getElementById(\"sub1\").style.display != \"block\") {\r\n        document.getElementById(\"sub1\").style.display = \"block\";\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(180deg)\";\r\n    } else {\r\n        document.getElementById(\"sub1\").style.display =\"none\";\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n    }\r\n}\r\n\r\nfunction change1(id){ \r\n    value1 = document.getElementById(id).getAttribute(\"value\");\r\n    gd1 = document.getElementById(id).innerHTML;\r\n    document.getElementById(\"sub1\").style.display=\"none\";\r\n    document.getElementById(\"s1\").innerHTML= gd1;\r\n    document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n}\r\nfunction hide() {\r\n    document.getElementById(\"sub1\").style.display = \"none\"\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n}\r\n\r\n</script>', '300x250', 'bg,copy,cta,logo', 'dropdown1_name_text,dropdown_width,dropdown_height,dropdown_fontsize,dropdown_top,dropdown_left,dropdown_bg_color,dropdown_color', '<script>\r\n    document.getElementById(\"dropdown1_name_text\").value = document.getElementById(\"s1\").innerText;\r\n    \r\n    document.getElementById(\"dropdown_width\").value = document.querySelector(\"#box_dp\").style.width.replace(/px/g, \'\');\r\n    document.getElementById(\"dropdown_height\").value = document.querySelector(\"#box_dp\").style.height.replace(/px/g, \'\');\r\n    document.getElementById(\"dropdown_fontsize\").value = document.querySelector(\"#s1\").style.fontSize.replace(/px/g, \'\');\r\n    document.getElementById(\"dropdown_top\").value = document.querySelector(\"#main_box\").style.top.replace(/px/g, \'\');\r\n    document.getElementById(\"dropdown_left\").value = document.querySelector(\"#main_box\").style.left.replace(/px/g, \'\');\r\n\r\n\r\n    document.getElementById(\"dropdown1_name_text\").addEventListener(\"keyup\",(e)=>{\r\n        document.getElementById(\"s1\").innerText = e.target.value\r\n    })\r\n\r\n    document.getElementById(\"dropdown_width\").addEventListener(\"keyup\",(e)=>{  \r\n        document.querySelector(\".dropdown\").style.width = `${Number(e.target.value)+20}px`;\r\n        document.querySelector(\".search2\").style.width = `${Number(e.target.value)+20}px`; \r\n          document.querySelector(\"#box_dp\").style.width = `${e.target.value}px`;\r\n    })\r\n    document.getElementById(\"dropdown_height\").addEventListener(\"keyup\",(e)=>{\r\n        document.querySelector(\"#box_dp\").style.height = `${e.target.value}px`;\r\n    })\r\n\r\n    document.getElementById(\"dropdown_fontsize\").addEventListener(\"keyup\",(e)=>{\r\n        document.querySelector(\"#s1\").style.fontSize = `${e.target.value}px`;\r\n        document.querySelectorAll(\"#dropdown li\").forEach(el =>{\r\n            el.style.fontSize = `${e.target.value}px`;\r\n        })\r\n    })\r\n\r\n    document.getElementById(\"dropdown_top\").addEventListener(\"keyup\",(e)=>{\r\n        document.querySelector(\"#main_box\").style.top = `${e.target.value}px`;\r\n    })\r\n    document.getElementById(\"dropdown_left\").addEventListener(\"keyup\",(e)=>{\r\n        document.querySelector(\"#main_box\").style.left = `${e.target.value}px`;\r\n    })\r\ndocument.getElementById(\"dropdown_bg_color\").addEventListener(\"input\",(e)=>{\r\n        document.querySelector(\"#box_dp\").style.backgroundColor = `${e.target.value}`;\r\n        document.querySelector(\".dropdown\").style.backgroundColor = `${e.target.value}`;\r\n        document.querySelector(\".search2\").style.backgroundColor = `${e.target.value}`;\r\n    })\r\ndocument.getElementById(\"dropdown_color\").addEventListener(\"input\",(e)=>{\r\n        document.querySelector(\"#s1\").style.color = `${e.target.value}`;\r\n        document.querySelectorAll(\"#dropdown li\").forEach(el =>{\r\n            el.style.color = `${e.target.value}`;\r\n        })\r\n    })\r\n</script>', '<script>\r\n    function insertCreative() {\r\n       \r\n        document.getElementById(\"sub1\").style.display =\"none\";\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n\r\n        var creative_code = $(\"#aspectRatio\").html();\r\n      $.ajax({\r\n        url: \"insertcreative.php\",\r\n        type: \"POST\",\r\n        dataType: \"JSON\",\r\n        data: {\r\n          id: id,\r\n          count: count,\r\n          content: creative_code.trim(),\r\n          fcat: fcat,\r\n          asset_r: localStorage.getItem(\"asset_req\"),\r\n        },\r\n        success: function (dt) {\r\n          console.log(dt);\r\n        },\r\n      });\r\n    }\r\n\r\n \r\n    if (localStorage.getItem(\"assets\") != \"\" && localStorage.getItem(\"assets\") != null) {\r\n      var asst_url = JSON.parse(localStorage.getItem(\"assets\"));\r\n      path = localStorage.getItem(\"path\");\r\n      count = localStorage.getItem(\"count\");\r\n      a_rq = localStorage.getItem(\"asset_req\");\r\n \r\n      var dim_url = path.split(\"/\")[2];\r\n      var dim_wd = dim_url.split(\"x\")[0];\r\n      var box_dy = document.getElementById(\"dynadata\" + dim_url);\r\n setTimeout(() => {\r\n    box_dy.innerHTML = \"\"; \r\n    async function loadApi() {\r\n                        var response = await fetch(\"https://publisherplex.io/selfserve/feed/multifeed.php?id=\"+feed_url);\r\n                        \r\n                        var data = await response.json();\r\n                        \r\n                        return data;\r\n                    }\r\n                    loadApi().then(res => {\r\n                        feed_data = res;\r\n                        document.getElementById(\"feed_dt\").value = JSON.stringify(feed_data);\r\n                        insertCreative();\r\n                        window.location.reload();\r\n                    })\r\n\r\n        asst_url.forEach((el) => {\r\n \r\n        var ast_id = el.Key.substring(el.Key.lastIndexOf(\"/\") + 1).split(\".\")[0];\r\n        a_rq.split(\",\").forEach(ar => {\r\n            if(ar == ast_id){\r\n                document.getElementById(\"dynadata300x250\").innerHTML += `<img id=\"${ast_id+dim_url}\" style=\"position:absolute;width:${dim_wd}px;left:0;top:0px;\" src=\"https://s.hcurvecdn.com/${el.Key}?v=${el.LastModified}\">`\r\n            }\r\n        }) \r\n        localStorage.setItem(\"assets\", \"\");\r\n        \r\n     });\r\n },2000);\r\n    }\r\n\r\n    setTimeout(()=>{\r\n    document.getElementById(\"deff_cy\").value = document.getElementById(\"city_name\").innerText\r\n    },6000)\r\n    \r\n </script>'),
