(551, 'lineiteme_dropdown', '<script type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/gsap/1.19.1/TweenMax.min.js\"></script>\r\n<style id=\"style0\">\r\n \r\n	#dynadata300x250{\r\n		width: 300px;\r\n		height: 250px;\r\n		position: absolute;\r\n		left:0px;\r\n		top:0px;\r\n		user-select: none;\r\n		border:1px solid black;\r\n		cursor: pointer;\r\n		overflow: hidden;\r\n		box-sizing: border-box;\r\n	}\r\n	\r\n	a{\r\n	    text-decoration: none;\r\n	}\r\n	\r\n	.search2{\r\n        width: 273px;\r\n        min-height: 58px;\r\n        max-height: 94px;\r\n        border-radius:5px;\r\n        z-index: 1000;\r\n        font-family: \"metroR\";\r\n        background-color: white;\r\n        cursor: pointer;\r\n        user-select: none;\r\n    }\r\n    .dropdown{\r\n        list-style-type: none;\r\n        padding: 0;\r\n        margin: 0;\r\n        overflow: auto;\r\n        width: 273px;\r\n        min-height: 35px;\r\n        max-height: 60px;\r\n        border: 1px solid rgba(0,0,0, .8);\r\n        background-color: white;\r\n        border-radius: 5px;\r\n   }\r\n\r\n    .dropdown  li  {\r\n        padding: 9px;\r\n        text-decoration: none;\r\n        font-size: 14px;\r\n        color: #000;\r\n        display: block;\r\n   }\r\n    li:hover{\r\n        background-color: #e8e8e8;\r\n   }\r\n   \r\n    .hr{\r\n        padding: 0 !important;\r\n        margin: 0 !important;\r\n        width:85%;\r\n        height:1px;\r\n        border-width:0;\r\n        color:gray;\r\n        background-color:#e8e8e8;\r\n        position:relative;\r\n        left:9px;\r\n    }\r\n\r\n    .dropdown::-webkit-scrollbar {\r\n        width: 5px;\r\n    }\r\n    .dropdown::-webkit-scrollbar-track {\r\n        background:  #EBEBEB;\r\n        border: 4px solid transparent;\r\n        background-clip: content-box;   \r\n    }\r\n    .dropdown::-webkit-scrollbar-thumb {\r\n        background:#99999A;\r\n    }\r\n    #box_dp{\r\n        cursor: pointer;\r\n    }\r\n</style>\r\n<div id=\"clickurl\">\r\n<div id=\"dynadata300x250\"> \r\n    <img style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/bg.png\" />\r\n    <img style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/logo.png\" />\r\n    <img id=\"copy\" style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/copy.png\" />\r\n    <img id=\"cta\" style=\"position:absolute;top:0px;left:0px;width:300px;\" src=\"https://s.hcurvecdn.com/tanishq/campaign1/300x600/dc9/cta.png\" />\r\n</div>\r\n      \r\n    \r\n    <div id=\"main_box\" onclick=\"event.preventDefault();event.stopPropagation()\" style=\"position: absolute;top: 80px;\">\r\n        \r\n        <div onclick=\"show1()\" id=\"box_dp\" style=\"display:flex; align-items:center; justify-content:space-between;padding:0 10px;border-radius: 5px;width:253px;height:30px;border:2px solid rgba(0,0,0, .7);font-size:11px;background-color:#fff;\">\r\n            \r\n        <div id=\"s1\" style=\"font-size:15px;color:rgba(0,0,0, .7);letter-spacing:1;font-family:aperm\">Choose your product</div>\r\n        <img id=\"arr1\" style=\"width: 17px;padding: 10px 7px 12px 7px;\" src=\"https://hcurvecdn.com/hoichoi/firstcampaign/300x250/DC7_F1_F2/down_arrow.png\">\r\n        </div>\r\n    \r\n        <div id=\"sub1\" class=\"search2\" style=\"z-index:102;text-decoration:none;display:none\">\r\n        <ul class=\"dropdown\" id=\"dropdown\"></ul>\r\n        </div>\r\n    </div>\r\n    <input type=\"hidden\" id=\"feed_dt\" />\r\n</div>\r\n<script type=\"text/javascript\">\r\n\r\nsetTimeout(() => {\r\n    var gd1 = \"\";\r\nvar value1 = \"\"\r\nvar i = 0;\r\nvar feed_data = JSON.parse(document.getElementById(\"feed_dt\").value);\r\nvar check = Object.keys(feed_data);\r\nvar dbmc;\r\nif(window.location.href.indexOf(\"dbmc\")!=-1){\r\n        dbmc = window.location.href.split(\"dbmc=\")[1].split(\"&\")[0]\r\n    }\r\n\r\n    if (check.includes(dbmc)) \r\n        {\r\n   var imp1=feed_data[dbmc][0][\"impression\"].split(\"[timestamp]\")[0]\r\nvar imp2=feed_data[dbmc][0][\"impression\"].split(\"[timestamp]\")[1]\r\n\r\nvar time = (new Date())/1000\r\n    var frame = document.getElementById(\"imptag\");\r\n    function trigIframe(){\r\n        var frame = document.createElement(\"iframe\")\r\n        frame.style[\"display\"] = \"none\"\r\n        frame.src = imp1+time+imp2\r\n        document.body.appendChild(frame)\r\n    }\r\n    setTimeout(trigIframe, 5000)\r\n    \r\n            document.getElementById(\"dropdown\").innerHTML = \"\";\r\n            feed_data[dbmc].forEach(el =>{\r\n                var li = document.createElement(\"li\");  \r\n                li.id=\"drop\"+i;\r\n                var atag = document.createElement(\"div\"); \r\n                atag.innerHTML=\"<span>\"+el.store+ \"</span><br>\";\r\n              li.setAttribute(\"onclick\",\"my1(`\"+el.url+\"`);event.preventDefault();event.stopPropagation()\");\r\n              li.appendChild(atag);\r\n              document.getElementById(\"dropdown\").appendChild(li);\r\n              document.getElementById(\"clickurl\").setAttribute(\"onclick\",\"my1(`\"+el.click+\"`);event.preventDefault();event.stopPropagation()\");\r\n                  i++;   \r\n        })\r\n}\r\n    else{\r\n        var lineitem_val = feed_data[Object.keys(feed_data)[0]][0].lineitem;\r\n\r\n        var imp1=feed_data[lineitem_val][0][\"impression\"].split(\"[timestamp]\")[0]\r\nvar imp2=feed_data[lineitem_val][0][\"impression\"].split(\"[timestamp]\")[1]\r\n\r\nvar time = (new Date())/1000\r\n    var frame = document.getElementById(\"imptag\");\r\n    function trigIframe(){\r\n        var frame = document.createElement(\"iframe\")\r\n        frame.style[\"display\"] = \"none\"\r\n        frame.src = imp1+time+imp2\r\n        document.body.appendChild(frame)\r\n    }\r\n    setTimeout(trigIframe, 5000)\r\n    \r\n            document.getElementById(\"dropdown\").innerHTML = \"\";\r\n            feed_data[lineitem_val].forEach(el =>{\r\n                var li = document.createElement(\"li\");  \r\n                li.id=\"drop\"+i;\r\n                var atag = document.createElement(\"div\"); \r\n                atag.innerHTML=\"<span>\"+el.store+ \"</span><br>\";\r\n              li.setAttribute(\"onclick\",\"my1(`\"+el.url+\"`);event.preventDefault();event.stopPropagation()\");\r\n              li.appendChild(atag);\r\n              document.getElementById(\"dropdown\").appendChild(li);\r\n              document.getElementById(\"clickurl\").setAttribute(\"onclick\",\"my1(`\"+el.click+\"`);event.preventDefault();event.stopPropagation()\");\r\n                  i++;   \r\n        })\r\n}\r\n}, 1000);\r\n\r\nfunction show1() {\r\n    if(document.getElementById(\"sub1\").style.display != \"block\") {\r\n        document.getElementById(\"sub1\").style.display = \"block\";\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(180deg)\";\r\n    } else {\r\n        document.getElementById(\"sub1\").style.display =\"none\";\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n    }\r\n}\r\n\r\nfunction change1(id){ \r\n    value1 = document.getElementById(id).getAttribute(\"value\");\r\n    gd1 = document.getElementById(id).innerHTML;\r\n    document.getElementById(\"sub1\").style.display=\"none\";\r\n    document.getElementById(\"s1\").innerHTML= gd1;\r\n    document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n}\r\nfunction hide() {\r\n    document.getElementById(\"sub1\").style.display = \"none\"\r\n        document.getElementById(\"arr1\").style.transform = \"rotate(0deg)\";\r\n}\r\n\r\n</script>', '300x250', 'bg,copy,cta,logo', 'dropdown1_name_text,dropdown_width,dropdown_height,dropdown_fontsize,dropdown_top,dropdown_left,dropdown_bg_color,dropdown_color,dropdown_fontFamily_text', '<script>\r\n    document.getElementById(\"dropdown1_name_text\").value = document.getElementById(\"s1\").innerText;\r\n    \r\n    document.getElementById(\"dropdown_width\").value = document.querySelector(\"#box_dp\").style.width.replace(/px/g, \'\');\r\n    document.getElementById(\"dropdown_height\").value = document.querySelector(\"#box_dp\").style.height.replace(/px/g, \'\');\r\n    document.getElementById(\"dropdown_fontsize\").value = document.querySelector(\"#s1\").style.fontSize.replace(/px/g, \'\');\r\ndocument.getElementById(\"dropdown_fontFamily_text\").value = document.querySelector(\"#s1\").style.fontFamily;\r\n    document.getElementById(\"dropdown_top\").value = document.querySelector(\"#main_box\").style.top.replace(/px/g, \'\');\r\n    document.getElementById(\"dropdown_left\").value = document.querySelector(\"#main_box\").style.left.replace(/px/g, \'\');\r\n\r\n\r\n    document.getElementById(\"dropdown1_name_text\").addEventListener(\"keyup\",(e)=>{\r\n        document.getElementById(\"s1\").innerText = e.target.value\r\n    })\r\n\r\n    document.getElementById(\"dropdown_width\").addEventListener(\"keyup\",(e)=>{  \r\n        document.querySelector(\".dropdown\").style.width = `${Number(e.target.value)+20}px`;\r\n        document.querySelector(\".search2\").style.width = `${Number(e.target.value)+20}px`; \r\n          document.querySelector(\"#box_dp\").style.width = `${e.target.value}px`;\r\n    })\r\n    document.getElementById(\"dropdown_height\").addEventListener(\"keyup\",(e)=>{\r\n        document.querySelector(\"#box_dp\").style.height = `${e.target.value}px`;\r\n    })\r\n\r\n    document.getElementById(\"dropdown_fontsize\").addEventListener(\"keyup\",(e)=>{\r\n        document.querySelector(\"#s1\").style.fontSize = `${e.target.value}px`;\r\n        document.querySelectorAll(\"#dropdown li\").forEach(el =>{\r\n            el.style.fontSize = `${e.target.value}px`;\r\n        })\r\n    })\r\n\r\n    document.getElementById(\"dro