                       document.getElementById(\"droplist\").innerHTML = \"\";\r\n                        for(let i=0;i<feed_data[city].length;i++){\r\n                          if(feed_data[city]){\r\n                            document.getElementById(\"droplist\").innerHTML += `<li onclick=my1(${feed_data[city][i][`lp`]});event.preventDefault();>${feed_data[city][i][`title`]}</li>` \r\n                          }\r\n                        }\r\n                      //   document.getElementById(\"default_city-text\").addEventListener(`keyup`,() => {\r\n                      //   var defcity = document.getElementById(\"default_city-text\").value;\r\n                      //   for(let i=0;i<feed_data[defcity].length;i++){\r\n                      //     if(feed_data[defcity]){\r\n                      //       document.getElementById(\"droplist\").innerHTML += `<li onclick=my1(${feed_data[defcity][i][`lp`]});event.preventDefault();>${feed_data[defcity][i][`title`]}</li>` \r\n                      //     }\r\n                      //   }\r\n                      //  })\r\n\r\n        \r\n                          \r\n})\r\n\r\n        \r\n        insertCreative();\r\n        localStorage.setItem(\"assets\", \"\");\r\n\r\n        document.getElementById(\"box\").addEventListener(\"click\",(e)=>{\r\n       \r\n   e.preventDefault();  \r\n   \r\n   if(document.getElementById(\"droppanel\").style.display == \"block\"){\r\n        document.getElementById(\"droppanel\").style.display=\"none\";\r\n        document.getElementById(\"arr2\").style.display =\"none\";\r\n       document.getElementById(\"arr1\").style.display =\"block\";\r\n   }\r\n   else{\r\n       document.getElementById(\"arr1\").style.display =\"none\";\r\n    document.getElementById(\"droppanel\").style.display=\"block\";\r\ndocument.getElementById(\"arr2\").style.display =\"block\";\r\n   }\r\n  \r\n})\r\n\r\nsetTimeout(() => {\r\n document.getElementById(\"droplist\").addEventListener(\"click\",(e)=>{\r\