(573, 'Motion_Graphics', '<script type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/gsap/1.19.1/TweenMax.min.js\"></script>\r\n<style>\r\n    #dynadata970x200,#dynadata970x200_2{\r\n        width:970px;\r\n        height:200px;\r\n        overflow:hidden;\r\n        border: 1px solid black;\r\n        position:absolute;\r\n        top: 0;\r\n        left: 0;\r\n        box-sizing: border-box;\r\n    }\r\n    #homescreen {\r\n        pointer-events: none;\r\n    }\r\n    \r\n</style>\r\n<a target=\"_blank\" id=\"clickurl\" href=\"\">\r\n<div id=\"dynadata970x200\"><img id=\"still300x600\" style=\"position:absolute;width:300px;\" src=\"https://s.hcurvecdn.com/apvideo/bo7oqb/300x600/still.webp?v=2023-03-20 14:14:42+00:00\"></div>\r\n\r\n<div id=\"wrap\" style=\"position: absolute; top: 0px; left: 0px; background: black; width: 970px; height: 200px;\">\r\n        <div id=\"progress\" style=\"position: absolute; background: rgb(255, 255, 255); width: 1.12903%; height: 3px; top: 0px; opacity: 0;\"></div>\r\n\r\n    <video style=\"margin-top: 0px; width: 970px; height: 200px;\" autoplay=\"\" htmlmediaelement.autoplay=\"true\" id=\"homescreen\" muted=\"true\" playsinline=\"\" poster=\"https://s.hcurvecdn.com/apvideo/bo7oqb/300x600/play.webp?v=2023-03-20 14:14:40+00:00\">\r\n            \r\n    <source src=\"https://hc-do.sgp1.cdn.digitaloceanspaces.com/apvideo/bo7oqb/300x600/video.mp4?v=2023-03-20 14:14:38+00:00\" type=\"video/mp4\">  \r\n        </video>\r\n\r\n       <div onclick=\"event.preventDefault();event.stopPropagation()\" style=\"width:100%;position:absolute;bottom:10px;text-align:right\">\r\n           <img onclick=\"replay(event)\" style=\"position:absolute;width:22px;right:10px;z-index:10000;\" src=\"https://s.hcurvecdn.com/discovery_plus/campaign1/replay.png\">\r\n            <img id=\"mute\" onclick=\"unmute(event)\" style=\"position:absolute;width:22px;right:10px;display:none;\" src=\"https://s.hcurvecdn.com/discovery_plus/campaign1/mute.png\">\r\n            <span>&nbsp;</span>\r\n        </div>\r\n    </div>\r\n<div id=\"dynadata970x200_2\"><img id=\"cta2300x600\" style=\"position:absolute;width:300px;\" src=\"https://s.hcurvecdn.com/apvideo/bo7oqb/300x600/cta2.webp?v=2023-03-21 06:53:06+00:00\"></div>\r\n</a>\r\n<div onclick=\"replay(event)\" style=\"width:30px;height:30px;background:black;opacity:0;left:0px;top:0px;position:absolute;z-index:1000;\">\r\n</div>\r\n<script>\r\n\r\n\r\nvar played = false;\r\nvar sound = false;\r\nvar vid = document.getElementById(\"homescreen\");\r\n\r\nfunction play(e){\r\n    if(played || vid.currentTime > 0){\r\n        console.log(\"manual play called\");\r\n        vid.muted = true;\r\n        return true;\r\n    }else{\r\n        e.preventDefault();\r\n        vid.play();\r\n        unmute();\r\n        return false;\r\n    }\r\n}\r\nvid.onended = function() {\r\n    played = true;\r\n    this.style[\"display\"] = \"none\";\r\n    document.getElementById(\"wrap\").style[\"display\"] = \"none\";\r\n};\r\n\r\nfunction updateProgress() {\r\n    var percent = (vid.currentTime*100/vid.duration) || 0;\r\n    document.getElementById(\"progress\").style[\"width\"] = percent+\"%\";\r\n    if(percent < 100) {\r\n        setTimeout(updateProgress,100);\r\n    }\r\n}\r\n\r\nupdateProgress()\r\n\r\nfunction replay(e){\r\n    if(e)e.preventDefault();\r\n    vid.muted=false;\r\n    sound = true;\r\n    document.getElementById(\"mute\").src = \"https://s.hcurvecdn.com/discovery_plus/campaign1/volume.png\";\r\n    vid.style[\"display\"] = \"block\";\r\n    document.getElementById(\"wrap\").style[\"display\"] = \"block\";\r\n    vid.currentTime = \"0\";\r\n    vid.play()\r\n}\r\nfunction unmute(e){\r\n    if(e)e.preventDefault();\r\n    if(!sound){\r\n        console.log(\"unmute\")\r\n        vid.muted=false\r\n        sound = true\r\n        document.getElementById(\"mute\").src = \"https://s.hcurvecdn.com/discovery_plus/campaign1/volume.png\"\r\n    }else{\r\n        console.log(\"mute\")\r\n        vid.muted=true\r\n        sound = false\r\n        document.getElementById(\"mute\").src =\"https://s.hcurvecdn.com/discovery_plus/campaign1/mute.png\"   \r\n    }\r\n    \r\n}\r\n\r\n\r\n</script>', '970x200', 'video,still,play,cta,copy,title', 'video_width,video_height,video_top,video_left', '<script>\r\n\r\n\r\ndocument.getElementById(\"video_width\").value = Number(document.getElementById(\"wrap\").style.width.replace(/px/g, \'\'))\r\ndocument.getElementById(\"video_height\").value = Number(document.getElementById(\"wrap\").style.height.replace(/px/g, \'\'))\r\ndocument.getElementById(\"video_top\").value = Number(document.getElementById(\"wrap\").style.top.replace(/px/g, \'\'))\r\ndocument.getElementById(\"video_left\").value = Number(document.getElementById(\"wrap\").style.left.replace(/px/g, \'\'))\r\n\r\ndocument.getElementById(\"video_width\").addEventListener(\"keyup\",(e)=>{\r\n    document.getElementById(\"wrap\").style.width = e.target.value+\"px\";\r\n    document.querySelector(\"video\").style.width = e.target.value+\"px\";\r\n})\r\n\r\ndocument.getElementById(\"video_height\").addEventListener(\"keyup\",(e)=>{\r\n    document.getElementById(\"wrap\").style.height = e.target.value+\"px\";\r\n    document.querySelector(\"video\").style.height = e.target.value+\"px\";\r\n})\r\n\r\ndocument.getElementById(\"video_top\").addEventListener(\"keyup\",(e)=>{\r\n    document.getElementById(\"wrap\").style.top = e.target.value+\"px\";\r\n})\r\n\r\ndocument.getElementById(\"video_left\").addEventListener(\"keyup\",(e)=>{\r\n    document.getElementById(\"wrap\").style.left = e.target.value+\"px\";\r\n})\r\n</script>', '<script>\r\n\r\n    function insertCreative(){\r\n        if(document.getElementById(\"wrap\") != null){\r\n                  if(document.getElementById(\"wrap\").style.display != \"\"){\r\n                  document.getElementById(\"wrap\").style.removeProperty(\"display\");\r\n                  document.getElementById(\"